  pagination: {
    el: ".swiper-pagination"
  }
});
// 3.4.2
var swiper = new Swiper(".swiper-container", {
  loop: true,
  speed: 800,
  autoplay: 2000,
  autoplayDisableOnInteraction: false,
  observer: true, //监听，当改变swiper的样式（例如隐藏/显示）或者修改swiper的子元素时，自动初始化swiper。
